function s(e,o=0){console.log("SCROLLING"),setTimeout(()=>{let t=document.getElementById(e);if(t){let l=t.getBoundingClientRect().top+window.pageYOffset-o;window.scrollTo({top:l,behavior:"smooth"})}else console.error(`Section with id '${e}' not found.`)},10)}document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll('a[href^="#"], [scroll-to]').forEach(o=>{o.addEventListener("click",t=>{t.preventDefault();let n=o.getAttribute("href").slice(1)||o.getAttribute("scroll-to");console.log(n);let l=o.getAttribute("scroll-offset")||88;s(n,l)})})});
