function a(o,t=0){console.log("SCROLLING"),setTimeout(()=>{let l=document.getElementById(o);if(l){let e=l.getBoundingClientRect().top+window.pageYOffset-t;window.scrollTo({top:e,behavior:"smooth"})}else console.error(`Section with id '${o}' not found.`)},10)}document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("a[data-href-scroll]").forEach(e=>{e.href=`${e.dataset.hrefPrefix+"#"+e.dataset.hrefScroll}`});let t=document.querySelectorAll('a[data-global-scroll="true"]');Array.from(t).filter(e=>e.href.includes(location.pathname)).forEach(e=>{let r=new URL(e.href);e.href=r.hash}),document.querySelectorAll('a[href^="#"], [scroll-to]').forEach(e=>{e.addEventListener("click",r=>{r.preventDefault();let c=e.getAttribute("href").slice(1)||e.getAttribute("scroll-to");console.log(c);let s=e.getAttribute("scroll-offset")||88;a(c,s)})})});
